insert into oneend.fm_recipe (fmrecipe_seq, fmrecipe_category, fmrecipe_cnt, fmrecipe_content, fmrecipe_file_name, fmrecipe_file_path, fmrecipe_ingre, fmrecipe_ready, fmrecipe_reg_date, fmrecipe_title, user_id_user_seq) values (1, '구이', null, '조리하기 뜨겁게 달군 팬이나 석쇠에 유장에 재운 더덕을 얹어서 양면을 2~3분 굽는다. 구워낸 더덕에 양념장을 바른다. 뜨겁게 달군 팬이나 석쇠에 ②의 더덕을 얹고 양면을 2~3분 구워 통깨를 뿌려서 낸다.', '678472f1-6a1b-40bf-87da-46347f7e2d90_스크린샷 2025-03-20 16.53.56.png', '/files/fmrecipe/678472f1-6a1b-40bf-87da-46347f7e2d90_스크린샷 2025-03-20 16.53.56.png', '재료 및 분량 (4인분) 더덕 480g, 통깨 1큰술, 식용유 ½컵 유장 간장 1큰술, 참기름 2큰술 양념장 간장 1큰술, 고추장 1큰술, 고춧가루 2큰술, 설탕 1큰술, 물엿 1큰술, 참기름 1큰술, 다진 파 1큰술, 다진 마늘 ½작은술, 깨소금 ½작은술', '더덕은 돌려가며 껍질을 벗긴 다음 반으로 갈라 방망이로 부서지지 않게 자근자근 두드린다. 두드린 더덕은 유장에 무쳐 10분 동안 재워둔다. 간장과 고추장에 나머지 재료를 섞어서 양념장을 만든다.', '2025-03-20 16:54:01.255615', '더덕 구이', null);
insert into oneend.fm_recipe (fmrecipe_seq, fmrecipe_category, fmrecipe_cnt, fmrecipe_content, fmrecipe_file_name, fmrecipe_file_path, fmrecipe_ingre, fmrecipe_ready, fmrecipe_reg_date, fmrecipe_title, user_id_user_seq) values (2, '구이', null, '뜨겁게 달군 팬에 식용유를 두르고 준비된 황태를 얹어 앞뒤로 노릇하게 굽는다. 양념장을 고루 발라서 5분 동안 재워둔다. 뜨겁게 달군 팬이나 석쇠에 얹고 타지 않게 구워낸다. 접시에 담고 송송 썬 실파와 실고추, 통깨를 뿌려서 낸다.', 'aaac4565-ddbd-46d5-8154-7783ef7ee436_스크린샷 2025-03-20 16.57.36.png', '/files/fmrecipe/aaac4565-ddbd-46d5-8154-7783ef7ee436_스크린샷 2025-03-20 16.57.36.png', '재료 및 분량 (4인분) 황태 2마리, 식용유 1큰술 양념장 간장 1큰술, 고추장 1큰술, 고춧가루 1½큰술, 물 3큰술, 설탕 ½큰술, 다진 파 1큰술, 다진 마늘 ½큰술, 다진 생강 ½작은술, 청주 1큰술, 깨소금 1작은술, 참기름 ½큰술, 후춧가루 약간 고명 송송 썬 실파, 통깨', '황태를 찬물에 넣었다 바로 건져서 5분 동안 그대로 놔둔다. 촉촉해진 황태는 가위로 머리와 지느러미를 잘라버리고 남은 가시를 발라낸다. 구울 때 껍질이 오그라들고 틀어지는 것을 막기 위해 황태 등 쪽에 잔칼집을 넣은 뒤 두 토막 낸다. 간장과 고추장에 나머지 재료를 섞어서 양념장을 만든다.', '2025-03-20 16:57:40.533886', '황태구이', null);
insert into oneend.fm_recipe (fmrecipe_seq, fmrecipe_category, fmrecipe_cnt, fmrecipe_content, fmrecipe_file_name, fmrecipe_file_path, fmrecipe_ingre, fmrecipe_ready, fmrecipe_reg_date, fmrecipe_title, user_id_user_seq) values (3, '구이', null, '조리하기 뜨겁게 달군 팬이나 석쇠에 유장에 재운 더덕을 얹어서 양면을 2~3분 굽는다. 구워낸 더덕에 양념장을 바른다. 뜨겁게 달군 팬이나 석쇠에 ②의 더덕을 얹고 양면을 2~3분 구워 통깨를 뿌려서 낸다.', '2562b20c-4885-4953-a1a9-fde786a82cf2_스크린샷 2025-03-20 17.03.03.png', '/files/fmrecipe/2562b20c-4885-4953-a1a9-fde786a82cf2_스크린샷 2025-03-20 17.03.03.png', '재료 및 분량 (4인분) 병새우 16마리(450g), 굵은소금 1컵 소금물 소금 1큰술, 물 1컵', '새우는 소금물에 씻어서 건진다.', '2025-03-20 17:03:15.786297', '새우구이', null);
insert into oneend.fm_recipe (fmrecipe_seq, fmrecipe_category, fmrecipe_cnt, fmrecipe_content, fmrecipe_file_name, fmrecipe_file_path, fmrecipe_ingre, fmrecipe_ready, fmrecipe_reg_date, fmrecipe_title, user_id_user_seq) values (4, '구이', null, '뜨겁게 달군 팬에 식용유를 두르고 유장에 재운 병어를 얹어 앞뒤로 굽는다. 구워낸 병어에 고추장 양념장을 붓으로 발라가며 약한 불에서 10분 동안 타지 않게 다시 굽는다.', '14724c23-264b-442d-baf8-80f2a461ebae_스크린샷 2025-03-20 17.04.40.png', '/files/fmrecipe/14724c23-264b-442d-baf8-80f2a461ebae_스크린샷 2025-03-20 17.04.40.png', '재료 및 분량 (4인분) 병어 1kg(4마리), 식용유 1큰술 유장 간장 2큰술, 참기름 1큰술 고추장 양념장 고추장 2큰술, 다진 파 2큰술, 다진 마늘 1큰술, 다진 생강 ½큰술, 청주 1큰술, 참기름 1큰술, 깨소금 1큰술', '병어는 비늘을 긁고 내장을 빼서 씻는다. 양면에 1cm 간격으로 칼집을 넣는다. 손질한 병어에 유장을 발라서 20분 동안 재워둔다. 고추장에 나머지 재료를 섞어서 고추장 양념장을 만든다.', '2025-03-20 17:04:45.364983', '병어 고추장 구이', null);
insert into oneend.fm_recipe (fmrecipe_seq, fmrecipe_category, fmrecipe_cnt, fmrecipe_content, fmrecipe_file_name, fmrecipe_file_path, fmrecipe_ingre, fmrecipe_ready, fmrecipe_reg_date, fmrecipe_title, user_id_user_seq) values (5, '구이', null, '갈비를 180℃로 예열한 오븐에 10분 정도 굽거나, 뜨겁게 달군 팬에 얹고 양면을 고루 익힌다. 그릇에 남은 양념장을 바르면서 굽는다.', 'c2076cca-17b8-44ab-87f7-b011b58de31c_스크린샷 2025-03-20 17.06.44.png', '/files/fmrecipe/c2076cca-17b8-44ab-87f7-b011b58de31c_스크린샷 2025-03-20 17.06.44.png', '재료 및 분량 (4인분) 갈비(두께 1cm 정도로 뼈째 두툼하게 절단) 1kg 고기 양념장 간장 5큰술, 설탕 2큰술, 다진 파 3큰술, 다진 마늘 1큰술, 깨소금 1큰술, 참기름 2큰술, 후춧가루 약간 선택 양념 배 ¼개(150g, 배즙 또는 육수 5큰술) 청주 2큰술', '갈비는 찬물에 담가 30분 정도 핏물을 뺀 후 물기를 없앤 뒤 기름 덩어리와 질긴 껍질을 떼어 손질한다. 고기 앞뒤로 잔칼집을 내어 부드럽게 만든다. 배는 껍질을 벗겨서 강판에 간다(선택 양념). 배 간 것에 간장과 모든 재료를 섞어서 고기 양념장을 만든다. 손질한 갈비에 고기 양념장을 넣고 주물러서 30분 동안 재워둔다.', '2025-03-20 17:06:50.886467', '소갈비 구이', null);
insert into oneend.fm_recipe (fmrecipe_seq, fmrecipe_category, fmrecipe_cnt, fmrecipe_content, fmrecipe_file_name, fmrecipe_file_path, fmrecipe_ingre, fmrecipe_ready, fmrecipe_reg_date, fmrecipe_title, user_id_user_seq) values (6, '구이', null, '뜨겁게 달군 석쇠나 팬에 재워둔 오리 고기를 얹고 노릇하게 구워낸다. 오리구이는 겨자장이나 쌈된장을 찍어서 채소에 싸 먹는다.', 'b860c372-8043-4280-bda3-b7950fd5cbee_스크린샷 2025-03-20 17.08.18.png', '/files/fmrecipe/b860c372-8043-4280-bda3-b7950fd5cbee_스크린샷 2025-03-20 17.08.18.png', '재료 및 분량 (4인분) 오리 고기(살코기) 500g, 소금 1작은술, 후춧가루 약간, 참기름 1큰술, 청주 1큰술 쌈된장 된장 ½컵, 고추장 ⅓컵, 다진 풋고추 ½컵, 참기름 1큰술, 깨소금 1작은술 겨자장 겨잣가루 4큰술, 물 4큰술, 식초 2큰술, 간장 ½큰술, 설탕 1½큰술', '오리 고기는 소금, 후춧가루, 참기름, 청주를 넣고 주물러서 20분 동안 재운다. 된장에 나머지 재료를 섞어서 쌈된장을 만든다. 겨잣가루에 물을 넣고 따뜻하게 10분 정도 두어 매운맛이 나면 식초, 소금, 설탕을 섞는다. 시판하는 연겨자에 식초, 설탕, 물을 넣어도 된다.', '2025-03-20 17:08:23.301103', '오리 구이', null);
insert into oneend.fm_recipe (fmrecipe_seq, fmrecipe_category, fmrecipe_cnt, fmrecipe_content, fmrecipe_file_name, fmrecipe_file_path, fmrecipe_ingre, fmrecipe_ready, fmrecipe_reg_date, fmrecipe_title, user_id_user_seq) values (7, '국', null, '뜨겁게 달군 냄비에 식용유를 두르고 다진 돼지고기와 양념장을 고루 볶는다. 돼지고기가 익으면 물 3½컵을 붓고 끓인다. 끓어오르면 준비한 순두부와 느타리버섯을 넣고 약한 불에서 10분쯤 끓인다. 소금으로 간을 맞춘다. 1인용 냄비에 끓인 순두부찌개를 담고 계란을 1개씩 깨 넣어 흰자만 익도록 끓여낸다. 또는 큰 냄비에 순두부찌개를 끓인 다음 계란을 모두 풀어 넣고 익혀서 덜어 먹어도 좋다.', '0efb9a8c-fe7b-46f0-b8fd-fd98bcdf10bf_스크린샷 2025-03-20 21.33.20.png', '/files/fmrecipe/0efb9a8c-fe7b-46f0-b8fd-fd98bcdf10bf_스크린샷 2025-03-20 21.33.20.png', '순두부 500g, 다진 돼지고기 100g, 느타리버섯 100g, 식용유 1큰술, 물 3컵(750ml), 소금 적당량, 계란 4개 양념장 고춧가루 1큰술, 참기름 1큰술, 다진 파 1큰술, 다진 마늘 1작은술, 국간장 1큰술, 물 2큰술', '순두부는 체에 쏟아 물기를 빼두었다가 큰 덩어리로 부순다. 느타리버섯은 잘게 찢어놓는다. 고춧가루에 참기름을 넣어 고루 섞은 뒤 나머지 재료를 섞어서 양념장을 만든다', '2025-03-20 21:33:49.746897', '순두부찌개', null);
insert into oneend.fm_recipe (fmrecipe_seq, fmrecipe_category, fmrecipe_cnt, fmrecipe_content, fmrecipe_file_name, fmrecipe_file_path, fmrecipe_ingre, fmrecipe_ready, fmrecipe_reg_date, fmrecipe_title, user_id_user_seq) values (8, '국', null, '냄비에 물을 넣고 고춧가루, 고추장을 풀어 센 불에서 끓인다. 국물이 끓어오르면 준비된 동태, 무, 콩나물과 청주, 국간장, 다진 파, 다진 마늘을 넣고 중간 불에서 20분 정도 끓인다. 두부, 버섯을 넣고 중간 불에서 10분쯤 더 끓이다가 실파를 넣고 소금으로 간한다. 입맛에 따라 후춧가루를 넣는다.', '14815f57-3d98-442d-b46f-f771f52ae340_스크린샷 2025-03-20 21.35.32.png', '/files/fmrecipe/14815f57-3d98-442d-b46f-f771f52ae340_스크린샷 2025-03-20 21.35.32.png', '동태 2마리, 고니 200g, 콩나물 90g, 무 120g, 두부 120g, 느타리버섯 60g, 실파 60g, 물 6컵(1.5L), 고춧가루 4큰술, 고추장 2큰술, 다진 마늘 1작은술, 다진 생강 ½작은술, 청주 1큰술, 소금·후춧가루 약간씩', '동태는 자연 해동 하거나 찬물에 녹여서 지느러미를 떼고 비늘을 벗겨낸다. 배를 갈라 내장을 꺼내고 깨끗이 씻은 다음 물기를 제거하고 5cm 길이로 토막 낸다. 고니는 씻어 건져서 물기를 제거한다. 콩나물은 꼬리를 떼고, 느타리버섯은 굵게 찢는다. 무와 두부는 3×2×1cm 크기로 썬다. 실파는 4cm 길이로 썬다.', '2025-03-20 21:35:37.340327', '동태찌개', null);
insert into oneend.fm_recipe (fmrecipe_seq, fmrecipe_category, fmrecipe_cnt, fmrecipe_content, fmrecipe_file_name, fmrecipe_file_path, fmrecipe_ingre, fmrecipe_ready, fmrecipe_reg_date, fmrecipe_title, user_id_user_seq) values (9, '국', null, '전골냄비에 준비된 돼지고기와 소시지, 채소를 고루 담고 베이크트 빈을 올린 다음 양념장을 얹는다. 소금으로 간한 국물을 붓고 센 불에서 끓이기 시작하여 팔팔 끓어오르면 스파게티면을 넣고 먹는다.', 'cddf8dbb-2ce1-4582-806e-148b27972eee_스크린샷 2025-03-20 21.36.42.png', '/files/fmrecipe/cddf8dbb-2ce1-4582-806e-148b27972eee_스크린샷 2025-03-20 21.36.42.png', '돼지고기(삼겹살) 150g, 소시지 200g, 햄 200g, 베이크트 빈 ½컵, 두부 150g, 실파 60g, 양파 1개(200g), 당근 60g, 팽이버섯 60g, 스파게티면 100g 국물 물(사골 국물 또는 멸칫국물) 6컵(1.5L), 소금 약간 양념장 고춧가루 2큰술, 국간장 1큰술, 고추장 1큰술, 청주 1큰술, 설탕 1큰술, 다진 마늘 1큰술, 다진 생강 1작은술, 후춧가루 약간, 물 1컵', '고춧가루에 나머지 재료를 섞어서 양념장을 만든다. 돼지고기는 3cm 폭으로 썰어서 양념장 1큰술을 덜어 넣고 무친다. 소시지는 2.5cm 두께로 어슷하게 썰고, 햄은 4×3×1cm 크기로 썬다. 양파는 반으로 갈라 0.6cm 두께로 채 썬다. 실파는 4cm 길이로 썰고, 당근은 4×2×0.3cm 크기로 납작하게 썬다. 팽이버섯은 밑동을 잘라두고, 스파게티면은 삶아서 준비한다.', '2025-03-20 21:36:46.865086', '부대찌개', null);
insert into oneend.fm_recipe (fmrecipe_seq, fmrecipe_category, fmrecipe_cnt, fmrecipe_content, fmrecipe_file_name, fmrecipe_file_path, fmrecipe_ingre, fmrecipe_ready, fmrecipe_reg_date, fmrecipe_title, user_id_user_seq) values (10, '국', null, '냄비에 물 2½컵을 넣고 된장을 덩어리 없이 풀어준 뒤 10분 동안 약한 불에서 끓여 된장 국물을 만든다. 끓는 된장 국물에 소고기를 넣고 끓이면서 위에 뜨는 거품은 걷어낸다. 끓는 국물에 썰어둔 감자와 양파를 넣고 약한 불에서 15분간 끓인다. 두부, 버섯, 풋고추와 다진 파, 다진 마늘, 고춧가루를 넣고 잠시 더 끓인다.', '903f9457-ce15-4d57-81f2-58683f946619_스크린샷 2025-03-20 21.37.45.png', '/files/fmrecipe/903f9457-ce15-4d57-81f2-58683f946619_스크린샷 2025-03-20 21.37.45.png', '소고기(등심) 100g, 두부 1모(300g), 감자 200g, 표고버섯(또는 양송이버섯) 3개(90g), 양파 1개(200g), 풋고추 4개(60g), 다진 파 1큰술, 다진 마늘 1작은술, 고춧가루 1작은술 된장 국물 물 2컵(500ml), 된장 4큰술', '소고기는 저며 썬다. 두부, 감자, 버섯은 1cm 폭으로 깍둑 썰고, 양파와 풋고추는 1×1cm 크기로 네모지게 썬다.', '2025-03-20 21:37:50.675710', '된장찌개', null);
insert into oneend.fm_recipe (fmrecipe_seq, fmrecipe_category, fmrecipe_cnt, fmrecipe_content, fmrecipe_file_name, fmrecipe_file_path, fmrecipe_ingre, fmrecipe_ready, fmrecipe_reg_date, fmrecipe_title, user_id_user_seq) values (11, '국', null, '큰 냄비에 식용유를 두르고 돼지고기를 볶다가 김치를 넣고 고기 표면이 갈색이 될 때까지 볶는다. 물 4컵을 붓고 약한 불로 줄여 20분간 끓인다. 김치가 부드러워지면 두부, 실파, 국간장, 설탕을 넣고 10분 정도 끓인다. 싱거우면 소금으로 간을 맞춘다.', '64cad317-7a2d-4539-94a9-d2bc3336b625_스크린샷 2025-03-20 21.38.42.png', '/files/fmrecipe/64cad317-7a2d-4539-94a9-d2bc3336b625_스크린샷 2025-03-20 21.38.42.png', '배추김치 500g, 돼지고기(삼겹살) 200g(또는 돼지갈비 400g), 식용유 4큰술, 물 4컵(1L), 두부 1모(300g), 실파 4½대(45g), 국간장 2큰술, 설탕 1큰술, 소금 약간', '배추김치는 잘 익은 것으로 준비하여 4cm 폭으로 네모지게 썬다. 돼지고기는 결 반대 방향으로 4cm 폭으로 저며 썬다. 두부는 4×3×1cm 크기로 납작하게 썬다. 실파는 3cm 길이로 썬다', '2025-03-20 21:38:48.446109', '김치찌개', null);
insert into oneend.fm_recipe (fmrecipe_seq, fmrecipe_category, fmrecipe_cnt, fmrecipe_content, fmrecipe_file_name, fmrecipe_file_path, fmrecipe_ingre, fmrecipe_ready, fmrecipe_reg_date, fmrecipe_title, user_id_user_seq) values (12, '밥', null, '멥쌀과 찹쌀, 삶은 팥과 불린 콩, 수수를 합하여 냄비에 넣고 고루 섞는다. 팥 삶은 물 1컵과 물 1½컵을 합하여 총 2½컵을 계량한 다음 소금간을 하여 밥물을 만든다. ①의 냄비에 밥물을 붓고 센 불에 올려 뚜껑을 닫고 끓인다. 밥이 끓어오르면 위에 차조를 얹고 중간 불로 줄여서 25분 동안 끓인다. 쌀알이 익어 퍼지면 불을 약하게 줄이고 5분간 뜸을 들인다. 주걱으로 위아래를 살살 섞어 그릇에 담는다.', 'e31db3dd-bb6b-4e40-95ea-41f046a3e6e8_스크린샷 2025-03-20 21.41.06.png', '/files/fmrecipe/e31db3dd-bb6b-4e40-95ea-41f046a3e6e8_스크린샷 2025-03-20 21.41.06.png', '멥쌀 2컵(450g), 찹쌀 1컵(220g), 팥 ¼컵, 검은콩 ½컵, 수수 ½컵, 차조 ¼컵 밥물 2½컵(625ml, 팥 삶은 물 1컵, 물 1½컵), 소금 ½큰술', '멥쌀과 찹쌀은 밥 짓기 30분 전에 씻어 물에 불렸다가 건진다. 팥은 깨끗이 씻어 냄비에 담고 충분히 잠길 정도로 물 2컵을 부어 불에 올린다. 끓어오르면 물은 따라 버리고, 다시 3컵 정도의 물을 부어 팥알이 터지지 않을 정도로 삶는다. 삶은 팥은 건지고 팥물은 따로 받아둔다. 콩은 씻어서 물에 2시간 동안 불렸다 건진다. 수수도 씻어서 물에 2시간 불리는 동안 여러 번 물을 갈아준다. 차조는 씻어서 건진다.', '2025-03-20 21:41:15.435052', '오곡밥', null);
insert into oneend.fm_recipe (fmrecipe_seq, fmrecipe_category, fmrecipe_cnt, fmrecipe_content, fmrecipe_file_name, fmrecipe_file_path, fmrecipe_ingre, fmrecipe_ready, fmrecipe_reg_date, fmrecipe_title, user_id_user_seq) values (13, '밥', null, '뜨겁게 달군 팬에 식용유를 두르고 양파, 청피망, 홍피망, 목이버섯을 순서대로 센 불에서 볶아낸다. 뜨겁게 달군 팬에 식용유를 조금 두르고 양념한 돼지고기를 서로 들러붙지 않게 볶아낸다. 냄비에 물 4컵을 붓고 중간 불에서 끓인다. 불린 당면을 넣고 5분간 삶아 건져서 물기를 뺀다. 팬에 식용유를 두르고 삶은 당면과 당면 양념을 넣어 5분간 볶는다. 녹말물을 고루 뿌려서 잘 어우러지게 섞고 참기름과 통깨를 뿌린다. 접시에 밥을 담고 한쪽에 잡채를 담아서 섞어 먹는다.', '48018278-88af-4c28-a856-4a1de026ada6_스크린샷 2025-03-20 21.42.23.png', '/files/fmrecipe/48018278-88af-4c28-a856-4a1de026ada6_스크린샷 2025-03-20 21.42.23.png', '흰밥 4공기(920g), 당면 100g(불린 것 250g), 돼지고기 120g, 마늘 2쪽, 청피망 ½개, 홍피망 ½개, 당근 30g, 양파 ½개(100g), 대파 1대, 목이버섯 4개, 녹말물 1½큰술(녹말 1큰술, 물 1큰술), 식용유·소금·참기름 적당량씩, 통깨 1큰술 고기 양념 간장 1큰술, 청주 1큰술 당면 양념 간장 1큰술, 설탕 ½큰술, 참기름 1큰술, 청주 1큰술, 후춧가루 약간', '흰밥을 준비한다. 마른 당면은 미지근한 물에 20분쯤 불린다. 돼지고기는 5×0.3×0.3cm 크기로 채 썰어서 간장, 청주로 밑간한다. 청피망, 홍피망, 당근은 5×0.3×0.3cm 크기로 채 썬다. 양파는 반으로 갈라서 채 썬다. 대파는 어슷하게 썰고, 마늘은 다진다. 목이버섯은 끓는 물에 5분쯤 불려서 손으로 큼직하게 뜯어둔다. 녹말에 물을 섞어서 녹말물을 만든다.', '2025-03-20 21:42:28.260898', '잡채덮밥', null);
insert into oneend.fm_recipe (fmrecipe_seq, fmrecipe_category, fmrecipe_cnt, fmrecipe_content, fmrecipe_file_name, fmrecipe_file_path, fmrecipe_ingre, fmrecipe_ready, fmrecipe_reg_date, fmrecipe_title, user_id_user_seq) values (14, '밥', null, '뜨겁게 달군 팬에 식용유 1큰술을 두르고 준비된 양파, 양배추, 당근, 고추, 대파를 센 불에서 양념장의 반을 넣고 반 정도 익게 볶아낸다. 뜨겁게 달군 팬에 식용유 1큰술을 두르고 마늘편, 돼지고기를 넣어 고기가 익을 때까지 센 불에서 볶은 후 ①의 채소와 섞는다. 그릇에 밥을 담고 제육볶음을 올린다.', '46072680-aebd-48a7-928f-e04cb5f4ad5a_스크린샷 2025-03-20 21.43.38.png', '/files/fmrecipe/46072680-aebd-48a7-928f-e04cb5f4ad5a_스크린샷 2025-03-20 21.43.38.png', '흰밥 4공기(920g), 돼지고기 300g, 양배추 150g, 양파 ½개, 풋고추(청양고추) 2개, 홍고추 1개, 당근 15g, 대파 1대, 마늘 4쪽, 식용유 2큰술 양념장 고추장 1큰술, 고춧가루 4큰술, 간장 2큰술, 설탕 1큰술, 물엿 2큰술, 청주 2큰술, 다진 마늘 1큰술, 생강즙 1작은술, 참기름 1큰술, 소금·후춧가루 약간씩', '고추장에 나머지 재료를 섞어서 양념장을 만든다. 돼지고기는 한 입 크기로 썰고 양념장의 반을 덜어서 무친다. 양배추, 당근, 양파는 3×1×0.3cm 크기로 썬다. 대파와 고추는 어슷하게 썰고, 마늘은 편으로 썬다.', '2025-03-20 21:43:44.082049', '제육덮밥', null);
insert into oneend.fm_recipe (fmrecipe_seq, fmrecipe_category, fmrecipe_cnt, fmrecipe_content, fmrecipe_file_name, fmrecipe_file_path, fmrecipe_ingre, fmrecipe_ready, fmrecipe_reg_date, fmrecipe_title, user_id_user_seq) values (15, '밥', null, '냄비에 콩나물을 반 깔고 고기를 얹은 뒤 쌀을 안친다. 다시 콩나물, 고기를 얹고 물 2컵을 붓는다. 처음엔 센 불에서 끓이다 콩나물 익는 냄새가 나면 약한 불에서 8분 정도 더 끓인다. 불을 끄고 10분간 뜸을 들인다. 밥이 다 되면 주걱으로 고루 섞는다. 그릇에 콩나물밥을 담고 양념 간장을 넣어서 비벼 먹는다.', 'eb07bbd1-da26-40ec-806a-4cae93691850_스크린샷 2025-03-20 21.44.40.png', '/files/fmrecipe/eb07bbd1-da26-40ec-806a-4cae93691850_스크린샷 2025-03-20 21.44.40.png', '멥쌀 2컵(450g), 물 2컵(500ml), 소고기(돼지고기) 살코기 100g, 청주 ½큰술, 간장 ½큰술, 콩나물 300g 양념 간장 간장 5큰술, 물 ½컵, 고춧가루 2큰술, 풋고추 다진 것 2큰술, 실파 15g, 다진 마늘 1작은술, 깨소금 1작은술, 참기름 1큰술, 소금 1작은술', '쌀은 깨끗이 씻어 30분 동안 불린 뒤 체에 건져 물기를 뺀다. 소고기나 돼지고기 살코기를 곱게 채 썰어 청주와 간장으로 밑간한다. 콩나물은 꼬리를 떼고 씻어서 건져둔다. 간장에 나머지 재료를 섞어서 양념 간장을 만든다.', '2025-03-20 21:44:46.374075', '콩나물밥', null);
insert into oneend.fm_recipe (fmrecipe_seq, fmrecipe_category, fmrecipe_cnt, fmrecipe_content, fmrecipe_file_name, fmrecipe_file_path, fmrecipe_ingre, fmrecipe_ready, fmrecipe_reg_date, fmrecipe_title, user_id_user_seq) values (16, '밥', null, '쌈장은 뜨겁게 달군 팬에 고기를 넣고 중간 불에서 볶다가 익으면 물 ¼컵을 붓고 된장, 고추장, 고춧가루, 다진 풋고추, 잣, 참기름을 넣어 중간 불에서 5분 정도 끓인다. 준비된 쌈에 밥을 넣고 쌈장을 올려서 싸 먹는다.', 'b6ff9b93-91a9-4f3f-95d0-f19b59b17360_스크린샷 2025-03-20 21.46.01.png', '/files/fmrecipe/b6ff9b93-91a9-4f3f-95d0-f19b59b17360_스크린샷 2025-03-20 21.46.01.png', '흰밥 4공기(920g) 쌈채소(350g) 상추, 쑥갓, 깻잎, 쌈케일, 청겨자 숙쌈(350g) 양배추, 호박잎 고기 쌈장 다진 소고기 50g, 물 ¼컵, 풋고추 2개(30g), 된장 4큰술, 고추장 2큰술, 고춧가루 1작은술, 참기름 2큰술, 잣 2큰술', '쌈채소는 흐르는 물에 씻고 소쿠리에 건져서 물기를 뺀다. 양배추와 호박잎은 김이 오른 찜통에 10분 정도 찐다. 전자레인지에 쪄도 좋다.', '2025-03-20 21:46:05.693351', '쌈밥', null);
insert into oneend.fm_recipe (fmrecipe_seq, fmrecipe_category, fmrecipe_cnt, fmrecipe_content, fmrecipe_file_name, fmrecipe_file_path, fmrecipe_ingre, fmrecipe_ready, fmrecipe_reg_date, fmrecipe_title, user_id_user_seq) values (17, '국', null, '뜨겁게 달군 냄비에 참기름을 두른 뒤 콩나물을 넣고 소금을 뿌린 다음, 물 ½컵을 넣고 뚜껑을 덮어 약한 불에서 10분 동안 끓인다. 남은 물을 더 붓고 중간 불에서 15분 동안 끓인다. 콩나물국이 끓으면 어슷 썬 파와 다진 마늘을 넣고 국간장과 소금으로 간을 맞추어 3분 정도 더 끓인다.', '6f2644c0-431f-4b27-84a4-0d5968e17c99_스크린샷 2025-03-20 21.47.21.png', '/files/fmrecipe/6f2644c0-431f-4b27-84a4-0d5968e17c99_스크린샷 2025-03-20 21.47.21.png', '재료 및 분량 (4인분) 콩나물 500g, 참기름 1큰술, 물 6컵(1.5L), 대파 ½대, 다진 마늘 1작은술, 국간장 2큰술, 소금 약간', '콩나물은 뿌리를 떼고 씻어서 물기를 뺀다. 대파는 어슷하게 썬다.', '2025-03-20 21:47:25.750544', '미역국', null);
insert into oneend.fm_recipe (fmrecipe_seq, fmrecipe_category, fmrecipe_cnt, fmrecipe_content, fmrecipe_file_name, fmrecipe_file_path, fmrecipe_ingre, fmrecipe_ready, fmrecipe_reg_date, fmrecipe_title, user_id_user_seq) values (18, '면', null, '큰 냄비에 물 16컵을 붓고 끓기 시작하면 국수를 넣는다. 거품이 생기면서 끓기 시작할 때 찬물 1컵을 붓고 국수를 저어준다. 3~4분간 삶아서 찬물에 여러 번 헹구어 1인분씩 사리를 지은 후 채반에 올려 물기를 뺀다. 각자의 그릇에 삶은 국수를 담고 뜨거운 멸치장국을 한 번 부었다가 쏟아 차가운 기운을 없앤다. 준비한 애호박, 당근, 지단을 얹고 간을 맞춘 뜨거운 멸치장국을 조심스럽게 부어 상에 바로 낸다.', '8843d46b-a2d2-44cd-bb67-e5f919939b98_스크린샷 2025-03-20 21.49.17.png', '/files/fmrecipe/8843d46b-a2d2-44cd-bb67-e5f919939b98_스크린샷 2025-03-20 21.49.17.png', '가는 밀국수(소면) 500g, 애호박 ½개(120g), 당근 ⅙개(30g), 계란 2개, 식용유·소금 적당량씩 멸치장국 국물용 멸치 20마리, 물 10컵(2.5L), 청주 1큰술, 국간장 2큰술, 소금 1큰술', '국물용 멸치의 머리와 내장을 떼어내 냄비에 넣고 물 10컵을 부어 센 불에서 끓인다. 끓어오르면 중간 불에서 20분 정도 더 끓인 뒤 면포를 얹은 체에 걸러 청주, 국간장, 소금을 넣고 간을 맞춘다. 애호박은 5cm 길이로 토막 내어 5×0.3×0.3cm 크기로 곱게 채 썬 다음 소금 ⅓작은술을 뿌려 30분간 절인다. 물기를 꼭 짜고 기름을 조금 두른 팬에 살짝 볶는다. 당근도 5cm 길이로 토막 내어 길이대로 곱게 채 썰어 식용유 ½작은술을 두른 팬에 센 불에서 1분 정도 볶은 후 소금으로 간한다. 계란은 깨뜨려서 흰자와 노른자를 나누어 약간의 소금을 넣고 각각 풀어놓는다. 풀어놓은 계란을 뜨겁게 달군 팬에 식용유를 두르고 다시 키친타월로 닦은 후 약한 불에서 얇게 황백 지단으로 부쳐 5cm 길이로 가늘게 채 썬다.', '2025-03-20 21:49:22.978537', '잔치국수', null);
insert into oneend.fm_recipe (fmrecipe_seq, fmrecipe_category, fmrecipe_cnt, fmrecipe_content, fmrecipe_file_name, fmrecipe_file_path, fmrecipe_ingre, fmrecipe_ready, fmrecipe_reg_date, fmrecipe_title, user_id_user_seq) values (19, '면', null, '큰 냄비에 물 16컵을 붓고 끓기 시작하면 국수를 넣는다. 거품이 생기면서 끓기 시작할 때 찬물 1컵을 붓고 국수를 젓는다. 3~4분간 삶아서 찬물에 여러 번 헹구어 채반에 올리고 물기를 뺀다. 큰 그릇에 삶은 국수를 모두 담은 뒤 비빔 양념장을 넣고 비벼서 1인분씩 국수 그릇에 담는다. 준비된 김치, 오이, 상추, 양파, 삶은 계란 반개를 올리고 구운 김을 부수어 고루 뿌린 뒤 상에 내어 비벼 먹는다.', '6e1adbad-515f-49ba-b3aa-de203ac51ca7_스크린샷 2025-03-20 21.50.22.png', '/files/fmrecipe/6e1adbad-515f-49ba-b3aa-de203ac51ca7_스크린샷 2025-03-20 21.50.22.png', '가는 밀국수(소면) 500g, 배추김치 500g, 오이 ½개(75g), 상추 4장, 양파 ½개(100g), 삶은 계란 2개, 구운 김 2장 비빔 양념장(초고추장) 고춧가루 1큰술, 고추장 4큰술, 간장 2큰술, 설탕 2큰술, 물엿 2큰술, 식초 4큰술, 다진 파 2큰술, 다진 마늘 1작은술, 참기름 1큰술, 깨소금 1큰술', '김치는 소를 털고 송송 썬 다음 꼭 짜서 참기름과 설탕을 넣고 무친다. 오이는 어슷하게 썰어서 가늘게 채 썬다. 상추는 깨끗이 씻어 물기를 빼고 1cm 두께로 굵게 채 썬다. 양파는 반으로 가르고 길이대로 곱게 채 썰어 찬물에 10분쯤 담갔다가 물기를 걷는다. 고춧가루에 나머지 재료를 섞어서 비빔 양념장을 만든다.', '2025-03-20 21:50:28.252124', '비빔국수', null);
insert into oneend.fm_recipe (fmrecipe_seq, fmrecipe_category, fmrecipe_cnt, fmrecipe_content, fmrecipe_file_name, fmrecipe_file_path, fmrecipe_ingre, fmrecipe_ready, fmrecipe_reg_date, fmrecipe_title, user_id_user_seq) values (20, '면', null, '식힌 소고기 국물과 동치미 국물을 1:1로 합하여 냉면 국물을 만든다. 큰 냄비에 물 16컵을 붓고 끓기 시작하면 메밀면을 넣는다. 거품이 생기면서 끓기 시작할 때 찬물 1컵을 붓고 메밀면을 저어준다. 3~4분간 삶아서 찬물에 여러 번 헹구고 1인분씩 사리를 지은 후 채반에 올려 물기를 뺀다. 삶은 메밀면을 1인분씩 대접에 담고 소고기 편육, 무, 오이, 배, 홍고추, 계란을 얹는다. 냉면 국물을 옆쪽에서 살며시 붓고, 매운맛을 내는 겨자장, 설탕, 식초를 따로 곁들여 낸다.', '7aeef96d-dd67-45d9-a0b0-17fd2f0fbaca_스크린샷 2025-03-20 21.51.33.png', '/files/fmrecipe/7aeef96d-dd67-45d9-a0b0-17fd2f0fbaca_스크린샷 2025-03-20 21.51.33.png', '냉면용 메밀면 450g, 무 200g, 오이 ½개(75g), 배 ¼개(150g), 홍고추 1개(15g), 삶은 계란 2개, 겨자장*·식초·설탕·적당량씩 소고기 육수 소고기(양지머리) 200g, 물 10컵(2.5L), 대파 1대(30g), 마늘 3쪽(15g), 통후추 1작은술 단촛물 소금 1큰술, 식초 4큰술, 설탕 4큰술 냉면 국물 동치미 국물, 5컵(1.25L), 소고기 육수 5컵(1.25L), 설탕 2큰술, 식초 2큰술, 겨자장 약간', '양지머리는 덩어리째 20분 동안 찬물에 담가둔다. 물 10컵을 냄비에 붓고 소고기, 파, 마늘, 통후추를 넣어 함께 끓인다. 고기가 익으면 건져서 0.3cm 두께로 썰어 편육을 만든다. ②의 육수는 체에 걸러낸 뒤 식혀서 기름을 걷어낸다. 무는 0.3cm 두께로 얇게 반달썰기해 식초, 설탕, 소금을 섞은 단촛물에 30분간 담가둔다. 오이는 길이대로 반 갈라 0.3cm 두께로 어슷하게 썬다. 소금 ½작은술을 뿌려 30분간 절였다가 물기를 꼭 짠다. 배는 껍질을 벗겨서 반으로 가른 후 무와 같은 크기로 썬다. 홍고추는 어슷 썬다. 계란은 노른자가 중심에 오도록 물에 담가서 12분 동안 저어가며 삶는다. 건져내어 찬물에 담가 껍질을 벗기고 세로로 반을 가른다.', '2025-03-20 21:51:39.683402', '물냉면', null);
insert into oneend.fm_recipe (fmrecipe_seq, fmrecipe_category, fmrecipe_cnt, fmrecipe_content, fmrecipe_file_name, fmrecipe_file_path, fmrecipe_ingre, fmrecipe_ready, fmrecipe_reg_date, fmrecipe_title, user_id_user_seq) values (21, '면', null, '식초에 절인 홍어를 꼭 짜서 그릇에 담는다. 양념장을 반만 덜어 미나리와 함께 무친다. 큰 냄비에 물 16컵을 붓고 끓기 시작하면 국수를 넣는다. 거품이 생기면서 끓기 시작할 때 찬물 1컵을 붓고 국수를 젓는다. 3~4분간 삶아서 찬물에 여러 번 헹구고 1인분씩 사리 지은 뒤 채반에 올려 물기를 뺀다. 삶은 국수에 나머지 양념장을 넣고 무친다. 냉면 그릇에 양념에 무친 냉면을 넣고 준비된 홍어무침과 채 썬 오이, 배와 계란을 얹는다. 기호에 맞게 양념장, 겨자 갠 것, 식초, 설탕을 넣어서 비벼 먹는다.', 'c2d9eb11-5220-4788-875c-6ca46d8b54c5_스크린샷 2025-03-20 21.53.41.png', '/files/fmrecipe/c2d9eb11-5220-4788-875c-6ca46d8b54c5_스크린샷 2025-03-20 21.53.41.png', '냉면용 메밀면 450g, 홍어(가오리) 240g, 식초 4큰술, 오이 ½개(75g), 미나리 60g, 배 ½개(300g), 삶은 계란 2개 국수 양념 간장 1큰술, 참기름 1큰술, 설탕 1작은술 회 양념장 고춧가루 6큰술, 고추장 1큰술, 간장 1큰술, 다진 파 2큰술, 다진 마늘 ½큰술, 깨소금 1큰술, 참기름 1큰술, 설탕 2큰술, 물엿 1큰술, 양파 간 것 2큰술 곁들임 겨자 갠 것, 식초, 설탕', '홍어는 채 썰고 찬물에 씻어 건져서 꼭 짠 다음 식초에 버무려 1시간 정도 재워둔다. 오이는 0.3cm 두께로 어슷하게 썰어서 가늘게 채 썬다. 배는 오이와 같은 크기로 채 썬다. 미나리는 다듬어서 4cm 길이로 썬다. 삶은 계란은 반으로 자른다. 고추장에 나머지 재료를 섞어서 양념장을 만든다.', '2025-03-20 21:53:47.967220', '회냉면', null);
insert into oneend.fm_recipe (fmrecipe_seq, fmrecipe_category, fmrecipe_cnt, fmrecipe_content, fmrecipe_file_name, fmrecipe_file_path, fmrecipe_ingre, fmrecipe_ready, fmrecipe_reg_date, fmrecipe_title, user_id_user_seq) values (22, '면', null, '큰 냄비에 닭고기 육수를 펄펄 끓이다가 칼국수면을 털어넣고 붙지 않게 저어가며 5분 정도 끓인다. 칼국수가 반쯤 익었을 때 불을 줄이고 준비한 닭고기 살과 애호박, 실파, 다진 마늘을 넣는다. 칼국수가 익을 때까지 5분간 더 끓인다. 삶은 칼국수를 각자의 대접에 담고 닭 국물을 붓는다. 양념 간장은 따로 곁들여 내어 기호에 맞추어 넣어 먹는다.', '1c70678e-0111-4557-87d0-a50fc20b3b0d_스크린샷 2025-03-20 21.56.43.png', '/files/fmrecipe/1c70678e-0111-4557-87d0-a50fc20b3b0d_스크린샷 2025-03-20 21.56.43.png', '칼국수용 건면 500g(또는 생면 600g), 애호박 ½개(75g), 실파 3대(30g), 다진 마늘 1큰술 닭고기 육수 닭 ½마리(500g), 대파 3대(90g), 생강 10g, 물 10컵(2.5L), 소금 2큰술, 후춧가루 약간 양념 간장 간장 3큰술, 물 4큰술, 고춧가루 1큰술, 다진 파 2큰술, 다진 마늘 1작은술, 다진 풋고추 2큰술', '닭은 깨끗이 씻어 닭, 대파, 생강, 물과 함께 30분 동안 뚜껑을 덮고 중간 불에서 끓인다. 닭이 익으면 불을 끄고 닭고기는 건져 식힌 후 살만 발라 가늘게 찢어놓는다. 위에 뜬 기름은 걷어내고 국물은 체에 거른다. 맑은 닭 육수에 소금과 후춧가루를 넣어 간을 한다. 양념 간장 재료를 모두 고루 섞어놓는다. 애호박은 0.3cm 두께로 동그랗게 썰어 채 썰고, 실파는 어슷 썬다.', '2025-03-20 21:56:47.247121', '칼국수', null);
insert into oneend.fm_recipe (fmrecipe_seq, fmrecipe_category, fmrecipe_cnt, fmrecipe_content, fmrecipe_file_name, fmrecipe_file_path, fmrecipe_ingre, fmrecipe_ready, fmrecipe_reg_date, fmrecipe_title, user_id_user_seq) values (23, '면', null, '메밀국수를 그릇에 담는다. 김치 썬 것, 무초생채, 오이채, 구운 김 부순 것, 계란, 깨소금을 올린다. 차게 둔 김치 국물을 가장자리에 붓고 비빔 양념장을 올린다. 기호에 맞게 식초, 설탕, 겨자를 곁들여 먹는다.', '9faa74bf-6263-48d7-bc9e-b2dc8d909c77_스크린샷 2025-03-20 21.58.51.png', '/files/fmrecipe/9faa74bf-6263-48d7-bc9e-b2dc8d909c77_스크린샷 2025-03-20 21.58.51.png', '메밀면 500g, 배추김치 120g, 오이 ½개(75g), 계란 2개, 구운 김 1장, 깨소금·천일염 약간씩, 김치 국물 2컵(물 1컵, 설탕 2큰술, 식초 2큰술, 국간장 1큰술) 무초생채 무 120g, 고운 고춧가루 1작은술, 식초 ½큰술, 설탕 ½큰술, 소금 ½작은술 비빔 양념장 홍고추 2개, 고춧가루 2큰술, 물 ⅓컵, 마늘 1쪽, 양파 ½개, 사과 ½개, 간장 1큰술, 참기름 1큰술, 깨소금 1큰술, 설탕 ½큰술, 소금 약간 곁들임 식초 2큰술, 설탕 1큰술, 겨자 2큰술', '큰 냄비에 물 16컵을 붓고 끓기 시작하면 국수를 넣는다. 거품이 생기면서 끓기 시작할 때 찬물 1컵을 붓고 국수를 젓는다. 3~4분간 삶아서 찬물에 여러 번 헹구고 1인분씩 사리 지은 뒤 채반에 올려 물기를 뺀다. 김치 국물에 물, 설탕, 식초, 국간장을 섞어 맛을 맞추고 냉장실에 넣어둔다. 무는 5×2.5×0.3cm 크기로 얇게 저며 썬다. 고운 고춧가루를 넣어 붉게 물들인 다음 식초와 설탕을 넣고 무친다. 오이는 천일염으로 문지르며 씻어서 0.3cm 두께로 어슷하게 썰었다가 다시 0.3cm 두께로 곱게 채 썬다. 계란은 냄비에 물과 함께 넣고 중간 불에서 12분 정도 삶아 익힌다. 익으면 찬물에 5분 정도 담가 식힌 다음 껍질을 벗기고 반으로 자른다. 비빔 양념장은 3시간 전 믹서에 모든 재료를 넣고 갈아둔다.', '2025-03-20 21:58:54.715781', '막국수', null);
insert into oneend.fm_recipe (fmrecipe_seq, fmrecipe_category, fmrecipe_cnt, fmrecipe_content, fmrecipe_file_name, fmrecipe_file_path, fmrecipe_ingre, fmrecipe_ready, fmrecipe_reg_date, fmrecipe_title, user_id_user_seq) values (24, '비빔밥', null, '비빔밥 그릇에 고슬고슬한 밥을 담고 그 위에 준비된 나물을 색 맞추어 돌려 담는다. 배채와 마늘편을 놓고 가운데에 육회를 올린 다음 그 위에 계란노른자를 얹는다. 비빔 양념장을 곁들여서 비벼 먹는다.', '3bf6804a-4149-446e-a61b-fbcf2db6e81b_스크린샷 2025-03-20 22.01.05.png', '/files/fmrecipe/3bf6804a-4149-446e-a61b-fbcf2db6e81b_스크린샷 2025-03-20 22.01.05.png', '흰밥 4공기(920g) 육회 소고기(등심, 채끝살) 180g, 배 ½개, 마늘(편) 4쪽, 계란노른자 4개, 김 가루 약간 육회 양념장 소금 ½작은술, 설탕 1작은술, 다진 파 1작은술, 다진 마늘 ⅓작은술, 깨소금 ½작은술, 참기름 1큰술 나물 시금치나물 60g, 청포묵무침 60g, 표고버섯 2개 비빔 양념장 간장(고추장) 2큰술, 고춧가루 1작은술, 다진 파 1작은술, 다진 마늘 ⅓작은술, 참기름 1큰술, 깨소금 1작은술', '소고기(우둔살이나 홍두깨살)는 기름이나 힘줄을 제거하고 얇게 떠서 가늘게 채 썬다. 분량의 재료를 모두 섞어서 육회 양념장과 비빔 양념장을 만든다. 채 썬 육회 고기에 육회 양념장을 넣어 무친다. 배는 껍질을 벗기고 가늘게 채 썰어 설탕물에 잠깐 담가두고, 마늘은 얇게 저며 썬다. 각색 나물을 준비한다.', '2025-03-20 22:01:11.214273', '육회비빔밥', null);
insert into oneend.fm_recipe (fmrecipe_seq, fmrecipe_category, fmrecipe_cnt, fmrecipe_content, fmrecipe_file_name, fmrecipe_file_path, fmrecipe_ingre, fmrecipe_ready, fmrecipe_reg_date, fmrecipe_title, user_id_user_seq) values (25, '비빔밥', null, '뜨겁게 달군 뚝배기에 참기름을 두르고 다진 마늘과 우렁을 넣어 5분 정도 볶다가 물 1컵을 붓고 중간 불에서 끓인다. 우렁을 넣은 국물이 끓어오르면 된장을 풀고 준비한 두부, 표고버섯, 양파, 풋고추, 호박을 넣어 약한 불에서 5분쯤 끓인다. 고춧가루, 대파를 넣는다. 밥에 부추와 우렁된장쌈장을 넣고 비벼 먹는다.', '83d08695-6a75-479e-a1e1-715a26019dc5_스크린샷 2025-03-20 22.02.35.png', '/files/fmrecipe/83d08695-6a75-479e-a1e1-715a26019dc5_스크린샷 2025-03-20 22.02.35.png', '멥쌀 2컵(450g), 보리 ½컵(110g), 물 3½컵, 부추 100g 우렁된장쌈장 우렁 150g, 밀가루 1큰술, 청주 1큰술, 된장 4큰술, 두부 50g, 표고버섯 2개(60g), 양파 50g, 호박 50g, 풋고추 1개, 대파 1대, 다진 마늘 1큰술, 고춧가루 1작은술, 참기름 1큰술, 물 1컵', '쌀과 보리쌀은 30분 정도 불려서 물 3½컵을 넣고 밥솥에 안친다. 센 불에서 끓이다 팔팔 끓어오르면 중간 불로 줄여 20분간 더 끓인다. 쌀알이 퍼지면 불을 약하게 줄이고 10분 동안 뜸을 들인다. 부추는 5cm 길이로 썬다. 우렁은 밀가루를 넣고 주물러서 찬물로 깨끗하게 씻은 다음 청주 1큰술을 뿌리고 30분간 놓아둔다. 두부는 도마에 놓고 칼로 으깬다. 표고버섯, 양파, 호박은 1cm 폭으로 깍둑썰기하고, 풋고추와 대파는 0.3cm 폭으로 곱게 다진다.', '2025-03-20 22:02:40.877103', '우렁된장비빔밥', null);
insert into oneend.fm_recipe (fmrecipe_seq, fmrecipe_category, fmrecipe_cnt, fmrecipe_content, fmrecipe_file_name, fmrecipe_file_path, fmrecipe_ingre, fmrecipe_ready, fmrecipe_reg_date, fmrecipe_title, user_id_user_seq) values (26, '전골', null, '뜨겁게 달군 전골냄비에 식용유를 두르고 양념장을 약한 불에서 살짝 볶는다. 불을 끄고 준비된 낙지, 소고기를 담은 뒤 사이사이에 채소를 골고루 담는다. 전골냄비에 국물을 붓고 중간 불에서 끓이다 낙지와 소고기가 익으면 약한 불로 줄이고 끓이면서 먹는다.', 'b8cbb7da-b4c1-464a-9763-d4d8788dd5ef_스크린샷 2025-03-20 22.03.54.png', '/files/fmrecipe/b8cbb7da-b4c1-464a-9763-d4d8788dd5ef_스크린샷 2025-03-20 22.03.54.png', '재료 및 분량 (4인분) 낙지 4마리, 소고기(등심) 200g, 식용유 2큰술, 양파 1개(200g), 양송이 100g, 실파 60g 낙지 양념 고춧가루 4큰술, 고추장 2큰술, 간장 1큰술, 설탕 1큰술, 다진 파 1큰술, 다진 마늘 ½큰술, 다진 생강 ¼작은술, 청주 1큰술, 깨소금 1큰술, 참기름 1큰술 고기 양념 간장 1큰술, 설탕 ½큰술, 다진 파 2작은술, 다진 마늘 1작은술, 참기름 1작은술, 깨소금 1작은술, 후춧가루 약간 전골 국물 물 6컵(1.5L), 국간장 2작은술, 소금 약간 불낙전골', '고기 양념과 낙지 양념을 만들어둔다. 낙지에 굵은소금을 뿌려가며 주물러서 찬물에 여러 번 씻는다. 5cm 길이로 썰고 낙지 양념 1큰술을 덜어서 무쳐둔다. 소고기는 0.3cm 두께의 불고기감으로 준비한다. 너무 길면 먹기 좋은 길이로 잘라서 고기 양념을 넣고 무쳐둔다. 양파는 길이대로 채 썬다. 양송이는 0.6cm 폭으로 썬다. 실파는 5cm 길이로 썬다. 전골 국물은 끓는 물에 간장과 설탕, 소금으로 간하여 만든다.', '2025-03-20 22:03:59.442574', '불낙전골', null);
insert into oneend.fm_recipe (fmrecipe_seq, fmrecipe_category, fmrecipe_cnt, fmrecipe_content, fmrecipe_file_name, fmrecipe_file_path, fmrecipe_ingre, fmrecipe_ready, fmrecipe_reg_date, fmrecipe_title, user_id_user_seq) values (27, '전골', null, '뜨겁게 달군 전골냄비에 기름을 두르고 양념한 소고기를 볶는다. 불을 끄고 채소와 두부를 고루 담은 뒤 장국을 부어 중간 불에서 끓인다. 채소가 익으면 약한 불로 줄인 상태에서 각자 덜어 먹는다.', '9eec8771-b726-4fdb-ab79-bea16305ef6f_스크린샷 2025-03-20 22.04.49.png', '/files/fmrecipe/9eec8771-b726-4fdb-ab79-bea16305ef6f_스크린샷 2025-03-20 22.04.49.png', '재료 및 분량 (4인분) 소고기(등심이나 우둔살) 450g, 두부 150g, 배추 150g, 새송이버섯 90g, 실파 45g, 당면 60 고기 양념 간장 3큰술, 설탕 1큰술, 다진 파 1큰술, 다진 마늘 1큰술, 참기름 1큰술, 깨소금 1큰술, 후춧가루 약간 전골 국물 물 6컵(1.5L), 간장 2큰술, 설탕 1큰술, 소금 약간 소고기전골', '간장에 나머지 재료를 섞어서 고기 양념을 만든다. 소고기는 0.3cm 두께의 불고기감으로 준비한다. 너무 길면 먹기 좋은 길이로 잘라서 고기 양념을 넣고 무쳐둔다. 두부는 4×3×1cm 크기로 썬다. 배추는 4cm 길이로 썬다. 새송이버섯은 5cm 길이로 썰어서 굵직하게 찢는다. 당면은 찬물을 잠길 정도로 부어서 불려둔다. 전골 국물은 끓는 물에 간장과 설탕, 소금으로 간하여 장국을 만든다.', '2025-03-20 22:04:53.883187', '소고기전골', null);
insert into oneend.fm_recipe (fmrecipe_seq, fmrecipe_category, fmrecipe_cnt, fmrecipe_content, fmrecipe_file_name, fmrecipe_file_path, fmrecipe_ingre, fmrecipe_ready, fmrecipe_reg_date, fmrecipe_title, user_id_user_seq) values (28, '전골', null, '전골냄비에 준비된 버섯과 소고기를 골고루 담고 그 위에 청피망과 홍피망을 올린다. 물을 붓고 센 불에서 끓이며 국간장과 소금으로 간한다. 고기가 다 익으면 약한 불로 줄인 상태에서 각자 덜어 먹는다.', 'dfdfaa77-b228-4a1a-a55f-032e3c186d89_스크린샷 2025-03-20 22.05.46.png', '/files/fmrecipe/dfdfaa77-b228-4a1a-a55f-032e3c186d89_스크린샷 2025-03-20 22.05.46.png', '재료 및 분량 (4인분) 각종 버섯(생표고, 새송이, 느타리, 팽이, 만가닥, 양송이) 200g, 소고기(등심) 150g, 청피망 60g, 홍피망 60g, 물 6컵(1.5L), 국간장 1큰술, 소금 약간 고기 양념 간장 1큰술, 설탕 ½큰술, 다진 파 1큰술, 다진 마늘 ½큰술, 참기름 1큰술, 후춧가루 약간 초간장 간장 4큰술, 식초 4큰술, 설탕 2큰술, 물 1큰술', '버섯은 흐르는 물에 씻어서 굵직하게 뜯거나 썰어둔다. 소고기는 0.3cm 두께의 불고기감으로 준비한다. 너무 길면 먹기 좋은 길이로 자르고, 핏물이 많으면 키친타월로 꼭꼭 눌러서 걷어낸다. 간장에 나머지 재료를 섞어서 만든 고기 양념으로 소고기를 재워둔다. 청피망과 홍피망은 4×1.2cm 크기로 썬다. 간장에 나머지 재료를 넣어서 초간장을 만든다.', '2025-03-20 22:05:50.371982', '버섯전골', null);
insert into oneend.fm_recipe (fmrecipe_seq, fmrecipe_category, fmrecipe_cnt, fmrecipe_content, fmrecipe_file_name, fmrecipe_file_path, fmrecipe_ingre, fmrecipe_ready, fmrecipe_reg_date, fmrecipe_title, user_id_user_seq) values (29, '국', null, '전골냄비에 배추 썬 것을 깔고 만두와 준비된 재료를 골고루 담는다. 매운 양념장을 얹고 가장자리에 전골 국물을 부은 다음 센 불에서 만두와 채소가 익을 때까지 끓인다. 끓이면서 각자 덜어 먹는다.', '1a841ef3-3ce9-4b29-825f-a5110a7f2faa_스크린샷 2025-03-20 22.06.54.png', '/files/fmrecipe/1a841ef3-3ce9-4b29-825f-a5110a7f2faa_스크린샷 2025-03-20 22.06.54.png', '재료 및 분량 (4인분) 만두 20개, 배추 150g, 양파 ½개(100g), 표고버섯 3개(90g), 대파 1대(30g) 전골 국물 소고기 100g, 국간장 2큰술, 마늘 2쪽, 물 6컵(1.5L), 소금 약간 매운 양념장 고춧가루 2큰술, 물 2큰술, 청주 1큰술, 국간장 1큰술, 다진 파 1큰술, 다진 마늘 1큰술, 소금·후춧가루 약간씩', '냄비에 소고기 썬 것과 간장, 마늘을 넣어 중간 불에서 볶다가 고기가 익으면 물을 붓고 약한 불에서 15분 정도 끓여 전골 국물을 만든다. 빚은 만두 또는 냉동 만두를 준비한다. 배추는 5×2.5cm 크기로 썬다. 표고버섯은 채 썰고, 대파는 어슷하게 썬다. 고춧가루에 나머지 재료를 섞어서 매운 양념장을 만든다.', '2025-03-20 22:06:58.390310', '만두전골', null);
insert into oneend.fm_recipe (fmrecipe_seq, fmrecipe_category, fmrecipe_cnt, fmrecipe_content, fmrecipe_file_name, fmrecipe_file_path, fmrecipe_ingre, fmrecipe_ready, fmrecipe_reg_date, fmrecipe_title, user_id_user_seq) values (30, '전골', null, '전골냄비에 배추 썬 것을 깔고 만두와 준비된 재료를 골고루 담는다. 매운 양념장을 얹고 가장자리에 전골 국물을 부은 다음 센 불에서 만두와 채소가 익을 때까지 끓인다. 끓이면서 각자 덜어 먹는다.', 'c1490149-d1a9-4af1-9607-3c87f8e98ea4_스크린샷 2025-03-20 22.08.43.png', '/files/fmrecipe/c1490149-d1a9-4af1-9607-3c87f8e98ea4_스크린샷 2025-03-20 22.08.43.png', '재료 및 분량 (4인분) 만두 20개, 배추 150g, 양파 ½개(100g), 표고버섯 3개(90g), 대파 1대(30g) 전골 국물 소고기 100g, 국간장 2큰술, 마늘 2쪽, 물 6컵(1.5L), 소금 약간 매운 양념장 고춧가루 2큰술, 물 2큰술, 청주 1큰술, 국간장 1큰술, 다진 파 1큰술, 다진 마늘 1큰술, 소금·후춧가루 약간씩', '냄비에 소고기 썬 것과 간장, 마늘을 넣어 중간 불에서 볶다가 고기가 익으면 물을 붓고 약한 불에서 15분 정도 끓여 전골 국물을 만든다. 빚은 만두 또는 냉동 만두를 준비한다. 배추는 5×2.5cm 크기로 썬다. 표고버섯은 채 썰고, 대파는 어슷하게 썬다. 고춧가루에 나머지 재료를 섞어서 매운 양념장을 만든다.', '2025-03-20 22:08:47.466847', '만두전골', null);
insert into oneend.fm_recipe (fmrecipe_seq, fmrecipe_category, fmrecipe_cnt, fmrecipe_content, fmrecipe_file_name, fmrecipe_file_path, fmrecipe_ingre, fmrecipe_ready, fmrecipe_reg_date, fmrecipe_title, user_id_user_seq) values (31, '전골', null, '뜨겁게 달군 전골냄비에 식용유를 두르고 중간 불에서 양념한 돼지고기를 볶다가 고기가 익으면 김치를 넣고 5분 정도 더 볶아서 잠시 불을 끈다. 준비된 채소와 떡을 골고루 놓고 그 위에 양념장을 얹은 다음 뜨거운 육수를 붓고 중간 불에서 끓인다. 준비한 채소와 떡을 끓여가면서 각자 덜어 먹는다.', '3d8670f0-3f9c-4ed4-b671-14d2181cbcf0_스크린샷 2025-03-20 22.09.44.png', '/files/fmrecipe/3d8670f0-3f9c-4ed4-b671-14d2181cbcf0_스크린샷 2025-03-20 22.09.44.png', '재료 및 분량 (4인분) 배추김치 450g, 돼지고기 100g, 식용유 2큰술, 두부 120g, 양파 1개(200g), 떡국 떡 150g, 물 6컵(1.5L) 매운 양념장 고추장 1큰술, 고춧가루 2큰술, 간장 1큰술, 청주 2큰술, 다진 마늘 1큰술, 다진 생강 ½작은술, 설탕 1큰술, 참기름 ½큰술', '김치전골 준비하기 고추장에 나머지 재료를 섞어서 매운 양념장을 만든다. 배추김치는 4cm 길이로 썬다. 돼지고기는 저며 썰고 양념장 1큰술을 덜어서 무친다. 두부는 4×3×1cm 크기로 썬다. 양파는 길이대로 채 썬다. 대파는 반으로 갈라 5cm 길이로 썰고, 부추는 5cm 길이로 썬다. 떡국 떡은 씻어서 건져둔다.', '2025-03-20 22:09:50.210814', '김치전골', null);
insert into oneend.fm_recipe (fmrecipe_seq, fmrecipe_category, fmrecipe_cnt, fmrecipe_content, fmrecipe_file_name, fmrecipe_file_path, fmrecipe_ingre, fmrecipe_ready, fmrecipe_reg_date, fmrecipe_title, user_id_user_seq) values (32, '국', null, '냄비에 참기름을 두르고 양념한 북어를 중간 불에서 볶다가 무채를 넣고 물을 부어 끓인다. 10분 정도 끓어서 국물이 뽀얗게 우러나면 국간장과 소금으로 간을 맞추고 홍고추와 대파를 넣는다. 계란 푼 것을 흘려 넣는다. 입맛에 따라 새우젓이나 고춧가루를 곁들인다.', '3eda8d84-2422-4f8f-a028-0e859dd2c3cd_스크린샷 2025-03-20 22.12.35.png', '/files/fmrecipe/3eda8d84-2422-4f8f-a028-0e859dd2c3cd_스크린샷 2025-03-20 22.12.35.png', '재료 및 분량 (4인분) 북어포(찢은 북어) 60g, 무 90g, 물 6컵(1.5L), 계란 2개, 대파 1대, 홍고추 1개, 참기름 1큰술, 국간장 1큰술, 소금 약간 북어포 양념 국간장 1큰술, 참기름 1큰술, 다진 마늘 ½큰술, 후춧가루 약간 곁들임 새우젓, 고춧가루', '찢은 북어를 찬물에 담갔다 바로 건져서 꼭 짜고 북어포 양념에 무친다. 무는 5×0.6×0.6cm 길이로 채 썬다. 홍고추는 반으로 갈라서 씨를 빼고 어슷하게 썬다. 대파도 어슷하게 썬다. 계란은 잘 풀어둔다.', '2025-03-20 22:12:40.285259', '북어국', null);
insert into oneend.fm_recipe (fmrecipe_seq, fmrecipe_category, fmrecipe_cnt, fmrecipe_content, fmrecipe_file_name, fmrecipe_file_path, fmrecipe_ingre, fmrecipe_ready, fmrecipe_reg_date, fmrecipe_title, user_id_user_seq) values (33, '국', null, '뜨겁게 달군 냄비에 참기름을 두른 뒤 콩나물을 넣고 소금을 뿌린 다음, 물 ½컵을 넣고 뚜껑을 덮어 약한 불에서 10분 동안 끓인다. 남은 물을 더 붓고 중간 불에서 15분 동안 끓인다. 콩나물국이 끓으면 어슷 썬 파와 다진 마늘을 넣고 국간장과 소금으로 간을 맞추어 3분 정도 더 끓인다.', 'f0a3e1f7-d5a4-4664-af13-5f95e9c2e4b5_스크린샷 2025-03-20 22.14.01.png', '/files/fmrecipe/f0a3e1f7-d5a4-4664-af13-5f95e9c2e4b5_스크린샷 2025-03-20 22.14.01.png', '재료 및 분량 (4인분) 콩나물 500g, 참기름 1큰술, 물 6컵(1.5L), 대파 ½대, 다진 마늘 1작은술, 국간장 2큰술, 소금 약간', '콩나물은 뿌리를 떼고 씻어서 물기를 뺀다. 대파는 어슷하게 썬다.', '2025-03-20 22:14:06.151781', '콩나물 국', null);
insert into oneend.fm_recipe (fmrecipe_seq, fmrecipe_category, fmrecipe_cnt, fmrecipe_content, fmrecipe_file_name, fmrecipe_file_path, fmrecipe_ingre, fmrecipe_ready, fmrecipe_reg_date, fmrecipe_title, user_id_user_seq) values (34, '국', null, '채 썬 오이에 국간장, 다진 파, 고춧가루를 넣고 무친다. 큰 그릇에 찬 생수를 붓고 식초, 설탕, 소금으로 새콤한 국물을 만든다. ②에 채 썬 오이를 넣고 섞어서 작은 그릇에 담아낸다.', 'a5941e83-3d06-4951-9fb0-f20ffe22764a_스크린샷 2025-03-20 22.16.17.png', '/files/fmrecipe/a5941e83-3d06-4951-9fb0-f20ffe22764a_스크린샷 2025-03-20 22.16.17.png', '재료 및 분량 (4인분) 오이 1개(150g) 오이 양념 국간장 1큰술, 다진 파 1작은술, 고춧가루 1작은술 냉국 찬물(생수) 6컵(1.5L), 식초 4큰술, 설탕 ½작은술, 소금 적당량', '오이는 깨끗이 씻어서 0.3cm 두께로 어슷하게 썬 다음 가늘게 채 썬다.', '2025-03-20 22:16:22.294261', '오이냉국', null);
insert into oneend.fm_recipe (fmrecipe_seq, fmrecipe_category, fmrecipe_cnt, fmrecipe_content, fmrecipe_file_name, fmrecipe_file_path, fmrecipe_ingre, fmrecipe_ready, fmrecipe_reg_date, fmrecipe_title, user_id_user_seq) values (35, '나물', null, '고사리나물 프라이팬에 기름을 두르고 고사리를 볶다가 물 4큰술을 넣고 뚜껑을 덮는다. 약한 불로 물이 없어질 때까지 익힌다. 깨소금, 참기름, 후춧가루를 넣고 다시 한번 볶아서 식힌다. 도라지나물 프라이팬에 식용유를 두르고 손질한 도라지를 넣어 볶다가 양념 재료를 모두 넣어 볶는다. 팬에 물 3큰술을 넣고 뚜껑을 덮어 약한 불로 물이 조금 남을 때까지 익힌다. 깨소금과 참기름을 넣고 고루 섞어서 식힌다. 그릇에 담고 실고추를 고명으로 얹어서 낸다.', '4007943d-3ae6-4e23-b813-a5427b8d571c_스크린샷 2025-03-20 22.17.41.png', '/files/fmrecipe/4007943d-3ae6-4e23-b813-a5427b8d571c_스크린샷 2025-03-20 22.17.41.png', '재료 및 분량 (4인분) 고사리나물 말린 고사리 45g(불린 후 300g) 식용유 1큰술 물 4큰술 양념 국간장 2큰술, 다진 파 1큰술, 다진 마늘 ½큰술, 깨소금 ½큰술, 참기름 1큰술, 후춧가루 약간 도라지나물 도라지 240g 물 ¼컵, 소금 1큰술 식용유 1큰술, 깨소금 ½큰술 참기름 ½큰술 양념 참기름 1큰술, 소금 ½큰술, 다진 파 1큰술, 다진 마늘 ½작은술, 국간장 ½큰술 고명 실고추 약간(선택 사항)', '고사리나물 말린 고사리는 찬물에 30분간 불려서 건져낸다. 끓는 물에 넣고 30분 동안 삶는다. 삶은 고사리를 5cm 정도로 잘라 국간장, 다진 파, 마늘을 넣고 버무려 20분 동안 둔다. 도라지나물 통도라지를 씻어서 5cm 길이대로 가늘게 썰어 볼에 담은 뒤 물 ¼컵과 소금 1큰술을 넣고 바락바락 주물러서 쓴맛을 뺀다. 끓는 물에 데쳐 찬물에 헹구고 물기를 짠다.', '2025-03-20 22:17:47.519817', '도라지나물', null);
